import React from 'react';
import Col from 'react-bootstrap/Col';
import {Paper} from "@material-ui/core";
import {
    Grid,
    VirtualTable,
    TableHeaderRow,
    TableFilterRow,
    TableSelection
} from "@devexpress/dx-react-grid-material-ui";
import {
    FilteringState,
    SortingState,
    IntegratedFiltering,
    IntegratedSorting,
    SelectionState
} from '@devexpress/dx-react-grid';

class PlayerTable extends React.Component {
    constructor(props) {
        super(props);
        this.columnHeaders = ["Name", "Country", "Club", "Age", "Position", "Overall", "Potential"];
        this.state = {
            rows: [],
            columns: [],
            selection: []
        }
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (prevProps.data !== this.props.data) {
            let rows = this.props.data.slice();
            let columns = this.columnHeaders.map(col => {
                return {name: col, title: col}
            });
            this.setState({
                rows: rows,
                columns: columns,
                selection: []
            });
        }
    }

    onSelectionChange = (selection) => {
        this.setState({
            rows: this.state.rows,
            columns: this.state.columns,
            selection: selection
        });
        this.props.onSelectionChange(this.state.rows[selection[selection.length-1]]);
    };


    render() {
        return (
            <Col>
                <Paper>
                    <Grid
                        rows={this.state.rows}
                        columns={this.state.columns}
                    >

                        <SortingState
                            defaultSorting={[{columnName: 'Name', direction: 'asc'}]}
                        />
                        <IntegratedSorting/>
                        <FilteringState defaultFilters={[]}/>
                        <IntegratedFiltering/>
                        <SelectionState
                            selection={this.state.selection}
                            onSelectionChange={this.onSelectionChange}
                        />
                        <VirtualTable/>
                        <TableHeaderRow showSortingControls/>
                        <TableSelection selectByRowClick highlightRow/>
                        <TableFilterRow/>
                    </Grid>
                </Paper>
            </Col>
        );
    }
}

export default PlayerTable;
